import pyspark.pandas as ps
import requests
from io import StringIO
import pandas as pd
import re
#Dataset under apache license: https://github.com/IBM/telco-customer-churn-on-icp4d/blob/master/LICENSE
csv = requests.get("https://raw.githubusercontent.com/IBM/telco-customer-churn-on-icp4d/master/data/Telco-Customer-Churn.csv").text
pdf = pd.read_csv(StringIO(csv), sep=",")
def cleanup_column(pdf):
  # Clean up column names
  pdf.columns = [re.sub(r'(?<!^)(?=[A-Z])', '_', name).lower().replace("__", "_") for name in pdf.columns]
  pdf.columns = [re.sub(r'[\(\)]', '', name).lower() for name in pdf.columns]
  pdf.columns = [re.sub(r'[ -]', '_', name).lower() for name in pdf.columns]
  pdf = pdf.rename(columns = {'streaming_t_v': 'streaming_tv', 'customer_i_d': 'customer_id'})
  

pdf = cleanup_column(pdf)
df = spark.createDataFrame(pdf)
df.write.mode("overwrite").option("overwriteSchema", "true").saveAsTable("demos.retail_churn_bronze")
# Convert to pandas on spark
p_onspark = df.pandas_api()

# OHE
data = ps.get_dummies(p_onspark, 
                      columns=['gender', 'partner', 'dependents',
                               'phone_service', 'multiple_lines', 'internet_service',
                               'online_security', 'online_backup', 'device_protection',
                               'tech_support', 'streaming_tv', 'streaming_movies',
                               'contract', 'paperless_billing', 'payment_method'],dtype = 'int64')

# Convert label to int and rename column
data['churn'] = p_onspark['churn'].map({'Yes': 1, 'No': 0})
data = data.astype({'churn': 'int32'})

# Drop missing values
data = data.dropna()
data = cleanup_column(data)
data.to_spark().write.mode("overwrite").option("overwriteSchema", "true").saveAsTable("demos.retail_churn_features")

# COMMAND ----------

pred = ""
pred = [int(i) for i in pred]

# COMMAND ----------

df = data.to_pandas()
df['prediction'] = pd.Series(pred)
df = df.dropna()
spark.createDataFrame(df).write.mode("overwrite").option("overwriteSchema", "true").saveAsTable("demos.retail_churn_predictions")
